import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [shift, setShift] = useState(3);
  const [result, setResult] = useState('');
  const [steps, setSteps] = useState([]);

  const handleEncrypt = async () => {
    const res = await axios.post('http://localhost:5000/encrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  const handleDecrypt = async () => {
    const res = await axios.post('http://localhost:5000/decrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  return (
    <div className="container mx-auto p-6">
      <div className="bg-white shadow-md rounded p-6 mb-6">
        <h1 className="text-xl font-bold mb-4">Caesar Cipher Encryption & Decryption</h1>
        <input
          className="border p-2 w-full mb-4"
          placeholder="Enter text..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <input
          type="number"
          className="border p-2 w-full mb-4"
          placeholder="Shift value"
          value={shift}
          onChange={(e) => setShift(Number(e.target.value))}
        />
        <div className="flex gap-2">
          <button onClick={handleEncrypt} className="bg-blue-500 text-white px-4 py-2 rounded">Encrypt</button>
          <button onClick={handleDecrypt} className="bg-green-500 text-white px-4 py-2 rounded">Decrypt</button>
        </div>
      </div>

      {result && (
        <div className="bg-white shadow-md rounded p-6">
          <h2 className="font-semibold mb-2">Result:</h2>
          <p className="text-lg mb-4">{result}</p>
          <h3 className="font-semibold mb-2">Steps:</h3>
          <ul className="list-disc list-inside">
            {steps.map((step, idx) => (
              <li key={idx}>{step}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
