import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [shift, setShift] = useState(3);
  const [result, setResult] = useState('');
  const [steps, setSteps] = useState([]);

  const handleEncrypt = async () => {
    const res = await axios.post('http://localhost:5000/encrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  const handleDecrypt = async () => {
    const res = await axios.post('http://localhost:5000/decrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-xl space-y-6">
        <h1 className="text-2xl font-bold text-center text-gray-800">
          Caesar Cipher Encryption & Decryption
        </h1>

        <div className="flex flex-col md:flex-row gap-4">
          <input
            className="border p-2 rounded flex-1"
            placeholder="Enter text..."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <input
            type="number"
            className="border p-2 rounded w-24"
            placeholder="Shift"
            value={shift}
            onChange={(e) => setShift(Number(e.target.value))}
          />
        </div>

        <div className="flex justify-center gap-4">
          <button
            onClick={handleEncrypt}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Encrypt
          </button>
          <button
            onClick={handleDecrypt}
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Decrypt
          </button>
        </div>

        {result && (
          <div className="bg-gray-50 p-4 rounded border mt-4">
            <h2 className="text-lg font-semibold text-gray-800">Result:</h2>
            <p className="text-md mb-2 text-gray-700">{result}</p>

            <h3 className="font-medium text-gray-800">Steps:</h3>
            <ul className="list-disc list-inside text-gray-700">
              {steps.map((step, idx) => (
                <li key={idx}>{step}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
