import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [shift, setShift] = useState(3);
  const [result, setResult] = useState('');
  const [steps, setSteps] = useState([]);

  const handleEncrypt = async () => {
    const res = await axios.post('http://localhost:5000/encrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  const handleDecrypt = async () => {
    const res = await axios.post('http://localhost:5000/decrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white shadow-xl rounded-xl p-8 max-w-2xl w-full space-y-6">
        <h1 className="text-3xl font-extrabold text-center text-gray-800">
          Caesar Cipher Encryption & Decryption
        </h1>

        <div className="flex flex-col md:flex-row gap-4 justify-center">
          <input
            className="border rounded px-4 py-2 flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Enter text..."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <input
            type="number"
            className="border rounded px-4 py-2 w-24 focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Shift"
            value={shift}
            onChange={(e) => setShift(Number(e.target.value))}
          />
        </div>

        <div className="flex gap-4 justify-center">
          <button
            onClick={handleEncrypt}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition"
          >
            Encrypt
          </button>
          <button
            onClick={handleDecrypt}
            className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 transition"
          >
            Decrypt
          </button>
        </div>

        {result && (
          <div className="bg-gray-50 border border-gray-200 rounded p-6">
            <h2 className="text-xl font-semibold mb-2 text-gray-800">Result:</h2>
            <p className="text-lg mb-4 text-gray-700">{result}</p>

            <h3 className="text-lg font-semibold text-gray-800">Steps:</h3>
            <ul className="list-disc list-inside text-gray-700">
              {steps.map((step, idx) => (
                <li key={idx}>{step}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
