import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [text, setText] = useState('');
  const [shift, setShift] = useState(3);
  const [result, setResult] = useState('');
  const [steps, setSteps] = useState([]);

  const handleEncrypt = async () => {
    const res = await axios.post('http://localhost:5000/encrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  const handleDecrypt = async () => {
    const res = await axios.post('http://localhost:5000/decrypt', { text, shift });
    setResult(res.data.result);
    setSteps(res.data.steps);
  };

  return (
    <div className="min-h-screen bg-[#1e1e2f] text-white flex items-center justify-center px-4">
      <div className="bg-[#2a2a40] p-8 rounded-lg shadow-lg w-full max-w-xl space-y-6">
        <h1 className="text-3xl font-bold text-center text-[#61dafb]">
          Caesar Cipher Encryption & Decryption
        </h1>

        <div className="flex flex-col md:flex-row gap-4">
          <input
            className="border border-gray-500 bg-white text-black p-2 rounded flex-1"
            placeholder="Enter text..."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <input
            type="number"
            className="border border-gray-500 bg-white text-black p-2 rounded w-24"
            placeholder="Shift"
            value={shift}
            onChange={(e) => setShift(Number(e.target.value))}
          />
        </div>

        <div className="flex justify-center gap-4">
          <button
            onClick={handleEncrypt}
            className="bg-blue-400 text-black font-semibold px-4 py-2 rounded hover:bg-blue-500 transition"
          >
            Encrypt
          </button>
          <button
            onClick={handleDecrypt}
            className="bg-green-400 text-black font-semibold px-4 py-2 rounded hover:bg-green-500 transition"
          >
            Decrypt
          </button>
        </div>

        {result && (
          <div className="bg-[#1e1e2f] border border-gray-600 p-4 rounded">
            <h2 className="text-lg font-semibold text-[#61dafb]">Result:</h2>
            <p className="text-md mb-2 text-white">{result}</p>

            <h3 className="font-medium text-[#61dafb]">Steps:</h3>
            <ul className="list-disc list-inside text-gray-300">
              {steps.map((step, idx) => (
                <li key={idx}>{step}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
